
public class ForLoopInJava 
{

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		
		// shortcut to create new class :- alt+shift+n 
				//loops in java
				// print numbers from 0 to 9
				/*syntax for loop
				for(intialization; condition ;incremenet or decrement operator)
				*/
				
				for(int i=0;i<10;++i)
				{
					System.out.println(i);
				}
	}

}
