package javaBasics;

public class DataTypesInJava 
{

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		
		// data types in Java
		
				//numbers
				int var1 ;
				var1=10;
				System.out.println(var1);
				
				int var2=20;
				System.out.println(var2);
				
				long l=123123;
				System.out.println(l);
			
				//percentage
				double d=61.88;
				System.out.println(d);
				 
				//true or false
				boolean b=true;
				boolean k=false;
				System.out.println(b+"  "+k);
				
				//char
				char c='u';
				
				//non primitive
				String str="java";
				String s="10+20/234, , , ,  +jashdkfjh @#$@#$ ";
				System.out.println(s);
				String s1="    ";
				String s2="10";
				System.out.println("+"+10+"");
				System.out.println(" \"10\"  ");
				int i=10,j=20,n=30;
				String s4="abc";
				System.out.println(s4+i+j+n);	
	}
}
